const Search = () => {

    const [userQuery, setQuery] = useState('')

//  ******************** debouncing
    const handleFilter = useCallback(_.debounce((userQuery, coinList, setFilteredCoins) => {
        //  Get all the coin symbols
        const coinSymbols = Object.keys(coinList)
        // Get all the coin names and map symbol to name

        const coinNames = coinSymbols.map(sym => coinList[sym].coinName)
        let allStringsToSearch = coinSymbols.concat(coinNames)
        console.log(allStringsToSearch)

    }, 500), [userQuery])

    useEffect(() => {
        handleFilter()

        return handleFilter.cancel;
    }, [userQuery, handleFilter])
// ****************-
    return (
        <AppContext.Consumer>
            {({setFileredCoins, coinList}) => 
                <Form>
                    <Title>Search</Title>
                    <SearchInput
                        value={userQuery}
                        onChange={(e) => setQuery(e.target.value)}
                        name="searc"
                        placeholder="search"
                        />
                </Form>
            }
        </AppContext.Consumer>


    )
}

export default Search
