import React from 'react'
import styled, { css } from 'styled-components'
import { Link, NavLink } from "react-router-dom";

const Bar = styled.div`
  color: var(--color-text);
  display: grid;
  grid-template-columns: 1fr 1fr ;
  align-items: center;

  a {
    color: inherit;
    text-decoration: none;
  }

  nav {
      display: flex; 
      justify-self: end;

      a {
        border-bottom: 2px solid transparent;
        transition: ease .2s all;
        padding: var(--s);
        margin: var(--m);
      }

      .active-link {
        border-bottom: 2px solid var(--color-main-darker);
      }
  }

`
const Logo = styled.span`
  color: var(--color-main-darker);
  justify-self: start;
  font-size: 1.2rem;
  font-weight: 700;
`

export default function(){
    return (
        <Bar>
            <Link to='/'><Logo>Ancrypto</Logo></Link>
            <nav>
                <NavLink activeClassName="active-link" exact to='/'>Dashboard</NavLink>
                <NavLink activeClassName="active-link" exact to='/settings'>Settings</NavLink>
            </nav>

        </Bar>
    )
}
