import styled from 'styled-components'
import { breakPoints } from '../Shared/GlobalTheme'

export default styled.div`
    display: grid;
    grid-template-rows: auto 1fr auto;
    grid-template-columns: 1fr;
    margin: 0 auto;
    max-width: 1400px;
    min-height: 100vh;

    > * {
        padding: var(--l);

    }

    @media(max-width:${breakPoints.mobile}) {

       > * {
            padding: var(--xl);
       } 
    }
`
