import {createGlobalStyle} from 'styled-components'

const breakpoints = {
  desktop: '1024px',
  tablet: '800px',
  mobile: '600px'
}


export default createGlobalStyle`

  :root {

    /* Colors */
    --color-success: green;
    --color-danger: red;
    --color-main: #99ddff;
    --color-main-light: #3fd2c7;
    --color-main-dark: #00458b;
    --color-background: white;
    --color-text: black;
    --color-text-secondary: gray;
    --color-text-negative: white;
    --color-background: white;
    --color-background-negative: white;

    --gradient-main: linear-graadient( to top left, #99ddff, white);
    /* Typography */
    --font-family: 'Fira Sans', sans-serif;
    /* Shadow */
    --shadow-normal: 1px 1px 5px rgba(0,0,0, 0.2);

    @media(max-width: ${breakpoints.tablet}) {
      :root {
        font-size: 13px;
      }
    })
  }


  body {
    margin: 0;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
      'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
      sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-family: var(--font-family);
    background: var(--gradient-main);
  }

  code {
    font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New',
    monospace;

  }


`

