import React, {useContext} from 'react'
import PriceGrid from './PriceGrid'
import CoinSpotlight from './CoinSpotlight'
import styled from 'styled-components'
import PriceChart from './PriceChart'
import {DataContext} from '../contexts'
import {H1, breakPoints} from '../Shared'


const ChartGrid = styled.div`
  width: 100%;
  display: grid;
  padding-top: var(--xl);
  grid-gap: var(--m);
  grid-template-columns: 1fr 3fr;
  align-items: center;
  justify-content: center;

  @media( max-width: ${breakPoints.tablet}) {
    grid-template-columns: 1fr;
  }
`
const Wrapper = styled.div`
height: 60vh;
display: flex;
align-items: center;
justify-content: center;
`

const FirstVisitorMessage = () => {
  return (
    <H1>
      Welcome!<br/><br/>
      To begin, please select your favourite coins from the Settings page.
    </H1>
  )
}

const loadingHandler = (coinList, prices) => {
  if (!coinList) {return <p>Loading Coins...</p>}
  if (!prices) {return <p>Loading Prices...</p>}
  return null
}

const Dashboard = () => {

  const {favourites, coinList, prices} = useContext(DataContext)

  return  !favourites.length 
    ? <FirstVisitorMessage />
    : !coinList || (prices.length !== favourites.length )
      ? <p>Loading...</p>
      : <>
          <PriceGrid />
          <ChartGrid>
            <CoinSpotlight />
            <PriceChart />
          </ChartGrid>
        </>
}

export default Dashboard
