const mainColor = window.getComputedStyle.getPropertyValue('--color-main-darker')

export default  {
  colors: [window.getComputedStyle.getPropertyValue('--color-main-darker'), '#50B432', '#ED561B', '#DDDF00', '#24CBE5', '#64E572',   
           '#FF9655', '#FFF263', '#6AF9C4'],
  chart: {
      backgroundColor: '#3486dd00'
  },
  title: {
      style: {
          color: '#000',
          font: 'bold 16px "Trebuchet MS", Verdana, sans-serif'
      }
  },
  xAxis: {
    gridLineWidth: 0
  },
  yAxis: {
    gridLineWidth: 0
  },
  credits: {
    enabled: false
  },
  subtitle: {
      style: {
          color: '#666666',
          font: 'bold 12px "Trebuchet MS", Verdana, sans-serif'
      }
  },
  legend: {
      itemStyle: {
          font: '9pt Trebuchet MS, Verdana, sans-serif',
          color: 'black'
      },
      itemHoverStyle:{
          color: 'gray'
      }   
  }
};
