import styled from 'styled-components'
import { breakpoints } from '../Shared/GlobalTheme'

export default styled.div`
    margin: 0 auto;
    max-width: 1400px;

    > * {
        padding: var(--m);

        @media(max-width:${breakpoints.phone}) {
            padding: var(--l);
        }
    }
`
