import styled from 'styled-components'
import React, { useState, useCallback, useEffect, useRef } from 'react'
import { breakPoints } from '../Shared/GlobalTheme'
import fuzzy from 'fuzzy'
import { AppContext } from '../App/AppProvider'
import _ from 'lodash'


const Title = styled.h2`
    color: var(--color-main-darker);
    font-size: var(--xl);
`
const Form = styled.form`
    width: 100%;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;
    flex-wrap: wrap;

`

const SearchInput = styled.input`
    border: 0;
    border: 1px solid var(--color-secondary-light);
    padding: var(--m) var(--l);
    box-shadow: var(--shadow);
    border-radius: var(--radius);
    margin: var(--s) 0;
    font-size: var(--mx);

    &:focus {
        border-color: var(--color-main)
    }

    @media(max-width: ${breakPoints.mobile}) {
        width: 100%;
    }
`


const handleFilter = _.debounce((userQuery, coinList, setFilteredCoins) => {
//  Get all the coin symbols
    const coinSymbols = Object.keys(coinList)
// // Get all the coin names and map symbol to name

    const coinNames = coinSymbols.map(sym => coinList[sym].CoinName)
    let allStringsToSearch = coinSymbols.concat(coinNames)
    console.log(allStringsToSearch)

}, 500)


function filterCoins(e, setFilteredCoins, coinList) {
    const userQuery = e.target.value;
    handleFilter(userQuery, coinList, setFilteredCoins);
}

// let fuzzyResults = fuzzy.filter(inputValue, allStringsToSearch, {})






const Search = () => {

    return (
        <AppContext.Consumer>
            {({setFilteredCoins, coinList}) => {
                return(
                    <Form>
                        <Title>Search</Title>
                        <SearchInput
                            onKeyUp={(e) => filterCoins(e, setFilteredCoins, coinList)}
                            name="searc"
                            placeholder="search"
                            />
                    </Form>

                )
            }
            }
        </AppContext.Consumer>


    )
}

export default Search
