import React, {useState} from 'react'

export const AppContext = React.createContext();

export default ({children}) => {
    const [page, setPage] = useState('dashboard');
    const [favourites, setFavourites] = useState('');
    const [firstVisit, setFirstVisit] = useState('true')

    const savedSettings = () => {
        let appData = JSON.parse(localStorage.getItem('ancrypto'));
        if(!appData) {
            return { page: 'settings', firstVisit: true}
        } else { return {}}
    }

    const confirmFavourites = () => {
        setPage('dashboard');
        setFirstVisit(false)
        localStorage.setItem('ancrypto', JSON.stringify({test: 'hello'}))
    }

    return (
    <AppContext.Provider value={{page, setPage, favourites, confirmFavourites}}>{children}</AppContext.Provider>
    )
}
